import React from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [data, setData] = React.useState(null);
  const [dataReturn, setDataReturn] = React.useState(null);
  const [inputHash, setInputHash] = React.useState('');

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  function postHash() {
    const requestOptions = {
      method: 'POST',
      body: inputHash
    };
    fetch('/postHashS', requestOptions)
        .then(response => response.json())
        .then(data => setDataReturn(data));
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <label>Hash : </label>
          <input value=Â {inputHash} onInput={e => setInputHash(e.target.value)}></input>.
          <button onClick={postHash}>Send</button>
        </div>
        <p>{!data ? "Loading..." : data}</p>
        <p>{dataReturn ? inputHash : dataReturn}</p>
      </header>
    </div>
  );
}

export default App;
