import React from "react";
import logo from './logo.svg';
import './App.css';
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Header from "./Header";

function App() {
  const [dataWelcome, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((dataWelcome) => setData(dataWelcome.message));
  }, []);

  return (
    <div className="App">
      <Header/>
      <header className="App-header">
        <div className="welcome">
          <h1>Welcome on GPUCrack !</h1>
          <NavLink to="/onehash"><button type="button" class="btn btn-primary">OneHash</button></NavLink>
          <NavLink to=""><button type="button" class="btn btn-primary">SeveralHash</button></NavLink>
        </div>
        <h2 class="server">{!dataWelcome ? "Loading..." : dataWelcome}</h2>
      </header>
    </div>
  );
}

export default App;
