import React from "react";
import logo from './logo.svg';
import './App.css';
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import Header from "./Header.js";

function App() {
  const [dataWelcome, setData] = React.useState(null);
  const [dataReturn, setDataReturn] = React.useState(null);
  const [inputHash, setInputHash] = React.useState('');
  let navigate = useNavigate();

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((dataWelcome) => setData(dataWelcome.message));
  }, []);

  function postHash() {
    if (isHash()) {
      /*fetch("/postHash", {
        method: 'POST',
        body: JSON.stringify({
          hash: 'stats'
        })
      })
        .then(response => response.json())
        .then(dataReturn => {
          setDataReturn(dataReturn.message);
          console.log(dataReturn);
          if (dataReturn === 'stats') navigate('/statistiques');
        });*/
        axios
          .post('/postHash', {hash: inputHash})
          .then((response) => {
            console.log(response.data.message);
            setDataReturn(response.data.message);
            if (dataReturn === 'stats') navigate('/statistiques');
          })
          .catch(err => {
            console.error(err);
          });
    }
  }

  function isHash() {
    return true;
  }

  return (
    <div className="App">
      <header className="App-header">
        <Header></Header>
        <div>
          <label>Hash : </label>
          <input value=Â {inputHash} onInput={e => setInputHash(e.target.value)}></input> 
          <button onClick={postHash}>Send</button>
          <NavLink to='/statistiques'>Stats</NavLink>
        </div>
        <p>{!dataWelcome ? "Loading..." : dataWelcome}</p>
      </header>
    </div>
  );
}

export default App;
