import React from "react";
import './OneHash.css';
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import Header from "./Header";

function OneHash() {
  const [dataWelcome, setData] = React.useState(null);
  const [dataReturn, setDataReturn] = React.useState(null);
  const [inputHash, setInputHash] = React.useState('');
  let navigate = useNavigate();

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((dataWelcome) => setData(dataWelcome.message));
  }, []);

  function postHash() {
    if (isHash()) {
        axios
          .post('/postHash', {hash: inputHash})
          .then((response) => {
            console.log(response.data.message);
            setDataReturn(response.data.message);
            if (dataReturn === 'stats') navigate('/statistiques');
          })
          .catch(err => {
            console.error(err);
          });
    }
  }

  function isHash() {
    return true;
  }

  return (
    <div className="App">
      <Header/>
      <header className="App-header">
        <div class="hash">
            <div>
                <label>Hash : </label>
                <input value=Â {inputHash} onInput={e => setInputHash(e.target.value)}></input> 
            </div>
          <button type="button" class="btn btn-primary" onClick={postHash}>Send</button>
        </div>
        <p>{!dataWelcome ? "Loading..." : dataWelcome}</p>
      </header>
    </div>
  );
}
 export default OneHash;
