import React from "react";
import logo from './logo.svg';
import './App.css';
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function App() {
  const [dataWelcome, setData] = React.useState(null);
  const [dataReturn, setDataReturn] = React.useState(null);
  const [inputHash, setInputHash] = React.useState('');
  let navigate = useNavigate();

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((dataWelcome) => setData(dataWelcome.message));
  }, []);

  function postHash() {
    const requestOptions = {
      method: 'POST',
      body: 'stats'
    };
    if (isHash()) {
      fetch('/postHash', requestOptions)
        .then(response => response.json())
        .then(dataReturn => {
          setDataReturn(dataReturn.message);
          if (dataReturn === 'stats') navigate('/statistiques');
        });
    }
  }

  function isHash() {
    return true;
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <label>Hash : </label>
          <input value=Â {inputHash} onInput={e => setInputHash(e.target.value)}></input> 
          <button onClick={postHash}>Send</button>
          <NavLink to='/statistiques'>Stats</NavLink>
        </div>
        <p>{!dataWelcome ? "Loading..." : dataWelcome}</p>
        <p>{dataReturn ? inputHash : dataReturn}</p>
      </header>
    </div>
  );
}

export default App;
