import React from "react";
import './App.css';
import { NavLink } from "react-router-dom";
import Header from "./Header";

function App() {
  const [dataWelcome, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((dataWelcome) => setData(dataWelcome.message));
  }, []);

  return (
    <div className="App">
      <Header/>
      <header className="App-header">
        <div className="welcome">
          <h1>Welcome on GPUCrack !</h1>
          <NavLink to="/onehash"><button type="button" class="btn btn-primary">OneHash</button></NavLink>
          <NavLink to="/result"><button type="button" class="btn btn-primary">SeveralHash</button></NavLink>
          </div>
      </header>
      <section class="py-5 text-white">
        <div class="container py-4">
          <div class="row">
            <div class="col-lg-8 mx-auto text-center">
              <h1 class="display-4">Bootstrap 4 megamenu</h1>
              <p class="lead mb-0">A very simple way to create Bootstrap 4 megamneu. </p>
              <p class="lead">Snippet by <a href="https://bootstrapious.com/snippets" class='text-white'><u>Bootstrapious</u></a>. </p>
            </div>
          </div>
          <div class="row pt-5">
            <div class="col-lg-10 mx-auto">
              <p class="lead">Use the default Bootstrap's dropdown menu to hold your megamneu.</p>
              <p class="lead">Set the <code>.dropdown</code> position to <code>static</code> instead of <code>absolute</code>.</p>
              <p class="lead">We use the class <code>.megamenu</code> to hold this <code>static</code> position.</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default App;
